mapper.select:
SELECT PE_ID, NAME, NICKNAME, TEL, EMAIL, ROADADDR, DETAILADDR
FROM PERSONAL
WHERE PE_ID=#{pe_Id}

mapper.update:
UPDATE PERSONAL
SET NICKNAME=#{nickName}, EMAIL=#{email}, ROADADDR=#{roadAddr}, DETAILADDR=#{detailAddr}
WHERE PE_ID=#{pe_Id}

mapper.select:
SELECT COUNT(*)
FROM PERSONAL
WHERE PE_ID=#{pe_Id} AND PW=#{pw}

mapper.update:
UPDATE PERSONAL
SET PW=#{pw}
WHERE PE_ID=#{pe_Id}
